import {
  Image,
  Platform,
  Pressable,
  ScrollView,
  StyleSheet,
  Text,
  StatusBar,
  TouchableOpacity,
  View,
} from "react-native";
import React, { useState, useEffect } from "react";
import { COLORS } from "../../constants/theme";
import { useTranslation } from "react-i18next";
import MaterialIcons from "react-native-vector-icons/MaterialIcons";
import Feather from "react-native-vector-icons/Feather";
import AntDesign from "react-native-vector-icons/AntDesign";
import { useNavigation } from "@react-navigation/native";
import FontAwesome from "react-native-vector-icons/FontAwesome";

const Help = () => {
  const { t } = useTranslation();
  const navigation = useNavigation();

  return (
    <View style={{ flex: 1 }}>
      {/* top bar and page title */}
      <View>
        <StatusBar backgroundColor={COLORS.primary} />
        <View style={[styles.header]}>
          <Text style={styles.title}>{t("common:help")}</Text>
          <TouchableOpacity
            onPress={() => navigation.navigate("deviceinfo")}
            style={{
              width: 35,
              height: 35,
              backgroundColor: COLORS.AltBlue,
              borderRadius: 10,
              justifyContent: "center",
              alignItems: "center",
            }}
          >
            <Feather name={"info"} size={25} color={COLORS.primary} />
          </TouchableOpacity>
        </View>
      </View>
      <ScrollView
        style={{
          paddingLeft: 20,
          paddingRight: 20,
          flex: 1,
          backgroundColor: COLORS.mainBlue,
        }}
        showsVerticalScrollIndicator={false}
        keyboardShouldPersistTaps="handled"
        contentContainerStyle={{
          paddingBottom: Platform.OS === "ios" ? 75 : 65,
        }}
      >
        <Text
          style={{
            fontSize: 18,
            fontWeight: "600",
            color: COLORS.primary,
            marginTop: 15,
          }}
        >
          {t("help:helptitle")}
        </Text>

        <TouchableOpacity
          onPress={() =>
            navigation.navigate("helpdetails", {
              topicId: 1,
              title: t("help:help1"),
            })
          }
          style={{
            width: "100%",
            height: 60,
            borderWidth: 1,
            borderRadius: 10,
            borderColor: COLORS.gray,
            backgroundColor: COLORS.white,
            marginTop: 15,
            justifyContent: "space-between",
            flexDirection: "row",
            paddingLeft: 15,
            paddingRight: 15,
            alignItems: "center",
          }}
        >
          <Text
            style={{
              fontSize: 20,
              fontWeight: "400",
              color: COLORS.primary,
            }}
          >
            {t("help:help1")}
          </Text>
          <MaterialIcons
            name={"keyboard-arrow-right"}
            size={30}
            color={COLORS.primary}
          />
        </TouchableOpacity>
        <TouchableOpacity
          onPress={() =>
            navigation.navigate("helpdetails", {
              topicId: 2,
              title: t("help:help2"),
            })
