            name={"keyboard-arrow-right"}
            size={30}
            color={COLORS.primary}
          />
        </TouchableOpacity>
        <TouchableOpacity
          onPress={() =>
            navigation.navigate("helpdetails", {
              topicId: 3,
              title: t("help:help3"),
            })
          }
          style={{
            width: "100%",
